this.ƒ = new Object();

this.ƒ.Forums = async (args) => {
    this._id = args._id;
    this.title = args.title;
    this.description = args.description;
    this.tags = args.tags;
    this.authors = args.authors;
    this.status = args.status;
    this.id = args.id;
    this.visiblity = args.visiblity;

    await this.__init__();
}

this.ƒ.Forums.prototype.__init__ = async () => {
    await this.Publish();
}

this.ƒ.Forums.prototype.Publish = async () => {
    $.ajax({
        type: 'POST',
        url: '/forums?_C=true',
        dataType: 'json',
        data: {
            _id: this._id,
            title: this.title,
            description: this.description,
            tags: this.tags,
            authors: this.authors,
            status: this.status,
            id: this.id,
            visiblity: this.visiblity
        }
    });
}